import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as l,d as a}from"./app-10abc819.js";const n={},s=a(`<h1 id="ゲーム設定を編集する" tabindex="-1"><a class="header-anchor" href="#ゲーム設定を編集する" aria-hidden="true">#</a> ゲーム設定を編集する</h1><p><code>game</code> フォルダの下に <code>config.txt</code> というファイルがあり、このファイルにゲームの関連情報を記入することができます（グラフィカルなエディタを使って直接編集することもできます）。</p><table><thead><tr><th style="text-align:left;">パラメータ</th><th style="text-align:left;">説明</th></tr></thead><tbody><tr><td style="text-align:left;">Game_name</td><td style="text-align:left;">ゲーム名</td></tr><tr><td style="text-align:left;">Game_key</td><td style="text-align:left;">ゲーム識別コード、長さ 6-10 文字、他のゲームと重複しないようにしてください</td></tr><tr><td style="text-align:left;">Title_img</td><td style="text-align:left;">タイトル画像、<code>background</code> フォルダに入れてください</td></tr><tr><td style="text-align:left;">Title_bgm</td><td style="text-align:left;">タイトルの BGM、<code>bgm</code> フォルダに入れてください</td></tr><tr><td style="text-align:left;">Game_Logo</td><td style="text-align:left;">ゲームのロゴ、複数表示することができ、| で区切ります</td></tr><tr><td style="text-align:left;">Enable_Appreciation</td><td style="text-align:left;">鑑賞機能（CGおよび背景音楽の鑑賞を含む）を有効にするかどうか。</td></tr><tr><td style="text-align:left;">Default_Language</td><td style="text-align:left;">デフォルト言語、「zh_CN」、「zh_TW」、「en」、「ja」、「fr」、「de」に設定可能。</td></tr><tr><td style="text-align:left;">Show_panic</td><td style="text-align:left;">パニックボタン機能を有効にするかどうか、true または false に設定</td></tr><tr><td style="text-align:left;">Legacy_Expression_Blend_Mode</td><td style="text-align:left;">Live2D の旧表情ブレンドモードを有効にするかどうか、true または false に設定</td></tr><tr><td style="text-align:left;">Max_line</td><td style="text-align:left;">テキスト表示フレームの最大行数</td></tr><tr><td style="text-align:left;">Line_height</td><td style="text-align:left;">テキストフレームの行高(単位:em)</td></tr></tbody></table><p>以下は設定ファイルの例です。</p><div class="language-text line-numbers-mode" data-ext="text"><pre class="shiki dracula" style="background-color:#282A36;" tabindex="0"><code><span class="line"><span style="color:#F8F8F2;">Game_name:WebGAL;</span></span>
<span class="line"><span style="color:#F8F8F2;">Game_key:0f33fdGr;</span></span>
<span class="line"><span style="color:#F8F8F2;">Title_img:Title.png;</span></span>
<span class="line"><span style="color:#F8F8F2;">Title_bgm:夏影.mp3;</span></span>
<span class="line"><span style="color:#F8F8F2;">Game_Logo:WebGalEnter.png|bg.png;</span></span>
<span class="line"><span style="color:#F8F8F2;">Enable_Appreciation:true;</span></span>
<span class="line"><span style="color:#F8F8F2;"></span></span></code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>`,5),d=[s];function i(r,c){return e(),l("div",null,d)}const g=t(n,[["render",i],["__file","config.html.vue"]]);export{g as default};
